import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
 
const Drink = (props) => (
 <tr>
   <td>{props.drink.name}</td>
   <td>{props.drink.position}</td>
   <td>{props.drink.level}</td>
   <td>
     <Link className="btn btn-link" to={`/edit/${props.drink._id}`}>Edit</Link> |
     <button className="btn btn-link"
       onClick={() => {
         props.deleteDrink(props.drink._id);
       }}
     >
       Delete
     </button>
   </td>
 </tr>
);
 
export default function DrinkList() {
 const [drinks, setDrinks] = useState([]);
 
 // This method fetches the records from the database.
 useEffect(() => {
   async function getDrinks() {
     const response = await fetch(`http://localhost:5005/drink`);
 
     if (!response.ok) {
       const message = `An error occurred: ${response.statusText}`;
       window.alert(message);
       return;
     }
 
     const drinks = await response.json();
     setDrinks(drinks);
   }
 
   getDrinks();
   return;
 }, [drinks.length]);
 
 // This method will delete a record
 async function deleteDrink(id) {
   await fetch(`http://localhost:5005/${id}`, {
     method: "DELETE"
   });
 
   const newDrinks = drinks.filter((el) => el._id !== id);
   setDrinks(newDrinks);
 }
 
 // This method will map out the records on the table
 function drinkList() {
   return drinks.map((drink) => {
     return (
       <Drink
       drink={drink}
         deleteDrink={() => deleteDrink(drink._id)}
         key={drink._id}
       />
     );
   });
   console.log(drinks)
 }
 
 // This following section will display the table with the records of individuals.
 return (
   <div>
     <h3>Drink List</h3>
     <table className="table table-striped" style={{ marginTop: 20 }}>
       <thead>
         <tr>
           <th>Drink Name</th>
           <th>Liquor</th>
           <th>Taste</th>
           <th>Rating</th>
         </tr>
       </thead>
       <tbody>{drinkList()}</tbody>
     </table>
   </div>
 );
}
